        insert into watering_winter values (2, 10, 'Поскольку цветение каланхоэ наступает в конце зимы, в холодные месяцы ему нужно организовать период покоя. Такое мероприятие пойдет на пользу растению и позволит ему накопить силы для закладывания бутонов.', 2);
        insert into watering_winter values (3, 4, 'Зимой растению не нужен обильный полив, в этот период желательно просто поддерживать почву в слегка увлажненном состоянии, чтобы она не пересыхала и не покрывалась трещинами. Весной цветок оживет, начнет выпускать новые побеги, и ему потребуется больше влаги. Пока температура воздуха не слишком высока, увлажнять землю можно, когда она просохнет на глубину 1 см. Обычно поливы требуются каждые 2-3 дня.', 1);
