        insert into fertilizers values (1, 'Феровит', 'Это одна из важных подкормок для азалии. Средство богатое на железо. Препарат способствует быстрому развитию растения и образованию новых побегов. Его можно использовать для полива и опрыскивания. Второй способ более эффективный.');
        insert into fertilizers values (2, 'Циркон', 'Это один из самых популярных препаратов, который используется для правильного развития корневой системы и обильного цветения. Продается средство в ампулах. Использовать жидкость надо в период полива. Для этого следует соединить отстоянную воду с несколькими каплями «Циркона». Применять средство стоит один раз в 4 дня.');
        insert into fertilizers values (3, 'НВ-101', 'Действующий и экономичный препарат. Достаточно 1-2 капли жидкости на лейку воды, чтобы насытить растение нужными микроэлементами. Им можно как опрыскивать, так и поливать цветы.');
        insert into fertilizers values (4, 'Добрая сила', 'Концентрированное комплексное удобрение, предназначенное для кактусов и разных видов суккулентов. В ней содержатся основные макроэлементы (азот-более 2%, фосфор – не менее 3,5 %, калий – не менее 4,5 %), а также гуманаты, необходимые растению микроэлементы, витамины и янтарная кислота. Применение этого удобрения не только обеспечивает каланхоэ полноценным питанием, но и повышает выносливость, укрепляет иммунитет.');
        insert into fertilizers values (5, 'Агрикола', 'Высококонцентрированное средство, которое содержит все необходимые суккулентам питательные вещества, причем, в хелатной форме. А потому усваивается растениями на 100 % и в кратчайшие сроки. Средство стимулирует рост растений, укрепляет иммунитет, помогает противостоять неблагоприятным факторам. Достаточно экономично в использовании – одной упаковки препарата (250 мл) хватает для приготовления 45 л жидкого удобрения.');
        insert into fertilizers values (6, 'Зола', 'Природная минеральная добавка поможет восполнить запасы калия. Ее используют в сухом виде – посыпая сверху, смешивая с грунтом. Или разводят в воде: на литр требуется 2 большие ложечки сухого вещества. Поливать герань таким раствором советуют каждые 14 дней. Он позволяет пеларгонии пышнее и продолжительнее цвести.');
        insert into fertilizers values (7, 'Апельсиновые корочки', 'Кожура этого цитруса содержит массу полезных микроэлементов, которые позволяют растению заложить много бутонов и обильно цвести. На литр кипятка берут горсть мелко нарезанных корочек. Настой будет готов через 24 ч.');
        insert into fertilizers values (8, 'Черный чай', 'Обычный черный чай нужно смешать с грунтом. Для этого необходимо взять одну часть заварки и 3 части земли. Такая смесь обеспечит фиалке надежную защиту от пересыхания земли, поскольку почва будет всегда оставаться рыхлой и легкой. Растение в таком субстрате начинает быстрее расти, улучшается и его внешний вид.');
        insert into fertilizers values (9, 'Кофейная гуща', 'Использование кофейной гущи может осуществляться двумя путями. Можно просто подливать гущу в горшок в качестве удобрения, а можно и смешать ее с землей для придания грунту большей рыхлости. Действие гущи практически идентично действию заварки, поэтому растение будет надежно защищено от пересыхания почвы.');
